mapper.insert:
INSERT INTO board(
    title
    , content
    , writer
    , password
)
VALUES (
    #{title}
    , #{content}
    , #{writer}
    , #{password}
)

mapper.update:
UPDATE board
SET title = #{title}
    , content = #{content}
    , writer = #{writer}
    , password = #{password}
